import axios from "axios";
import React, { Component } from "react";


export default class StudentList extends Component {

    constructor(){

        super();
        this.state = {
            studenList :[]
        };


    }

    componentDidMount(){
        axios.get('http://localhost:3000/posts')
        .then( (response) => {
                this.setState = response.data
                // console.log(this.setState)

        })
        .catch((error) => {
            console.log(error)
        })
    }



    render() { 


        return (
            <div className="companylist bg-dark">

            {this.state.studenList.map(companyinfo => 
                    <div>{companyinfo.data}</div>

                    )}
            </div>

        )


        }
}
